// MOD : MSSTI Quick Language (v1.0.1) - START
	if (!defined('IN_QUICK_LANGUAGE'))
	{
		include($phpbb_root_path . 'includes/function_quick_language.' . $phpEx);
	}
	$user['quick_language'] = quick_language::quick_language_setlang( $user['user_lang'], $user['user_id'], $config['cookie_name'] );
	// if the user change his language, update it 
	if ( $user['user_lang'] != $user['quick_language'] )
	{
		$user['user_lang']		= $user['quick_language'];
	}
// MOD : MSSTI Quick Language (v1.0.1) - END

